mapper.select:
SELECT TP_SEQ, TP_ID, TP_LOCATION, TP_TITLE, TP_CONTENT, TP_WRITEDATE
FROM TRAVEL_PLAN
WHERE TP_SEQ = ${tpSeq}

mapper.update:
UPDATE TRAVEL_PLAN
SET TP_LOCATION = #{tpLocation}, TP_TITLE = #{tpTitle}, TP_CONTENT = #{tpContent}, TP_FILENAME = #{tpFileName}
WHERE TP_SEQ = ${tpSeq}

mapper.select:
SELECT COUNT(*)
FROM TP_LIKES
WHERE TP_SEQ=${tpSeq}

SELECT COUNT(*)
FROM TP_LIKES
WHERE TP_SEQ = ${tpSeq} AND TP_ID = #{tpId}

mapper.update:
UPDATE TRAVEL_PLAN
SET TP_LIKECOUNT = TP_LIKECOUNT - 1
WHERE TP_SEQ = ${tpSeq}

mapper.delete:
DELETE
FROM TP_LIKES
WHERE TP_ID = #{tpId} AND TP_SEQ = ${tpSeq}

mapper.update:
UPDATE TRAVEL_PLAN
SET TP_LIKECOUNT = TP_LIKECOUNT + 1
WHERE TP_SEQ = ${tpSeq}
